import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const Search = ({ setCountry }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [country, setCountryState] = useState('us'); // Default country is 'us'
  const navigate = useNavigate();
  const location = useLocation();
  
  // Extract search query and country from URL
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const q = params.get('q');
    const c = params.get('country');
    if (q) setSearchQuery(q);
    if (c) setCountryState(c);
  }, [location]);

  // Handle form submission
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setCountry(country);
      navigate(`/search?q=${searchQuery}&country=${country}`);
    }
  };

  return (
    <form className="d-flex" onSubmit={handleSearchSubmit} role="search">
      <input
        className="form-control me-2"
        type="search"
        placeholder="Search"
        aria-label="Search"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <select
        className="form-control me-2"
        value={country}
        onChange={(e) => setCountryState(e.target.value)}
      >
        <option value="us">USA</option>
        <option value="in">India</option>
        <option value="gb">UK</option>
        {/* Add more country options as needed */}
      </select>
      <button className="btn btn-outline-success" type="submit">Search</button>
    </form>
  );
};

export default Search;
