import React, { Component } from 'react';
import PropTypes from 'prop-types';
import NewsItems from './NewsItems'; // Import your NewsItems component
import Spinner from './Spinner'; // Import Spinner if you're using one for loading
import { useLocation } from 'react-router-dom'; // For accessing query params

class News extends Component {
  state = {
    articles: [],
    loading: true,
  };

  static propTypes = {
    country: PropTypes.string,
    category: PropTypes.string,
    setProgress: PropTypes.func,
  };

  // Fetch the news data based on query and country
  async componentDidMount() {
    const { searchQuery, country } = this.props;
    this.props.setProgress(10);
    const url = `https://newsapi.org/v2/everything?q=${searchQuery}&country=${country}&apiKey=YOUR_API_KEY`; // Replace with actual API URL

    try {
      const response = await fetch(url);
      const data = await response.json();
      this.setState({ articles: data.articles, loading: false });
      this.props.setProgress(100);
    } catch (error) {
      console.error(error);
      this.setState({ loading: false });
    }
  }

  render() {
    const { loading, articles } = this.state;

    return (
      <div>
        {loading ? (
          <Spinner />
        ) : (
          <div className="news-container">
            {articles.map((article) => (
              <NewsItems key={article.url} article={article} />
            ))}
          </div>
        )}
      </div>
    );
  }
}

export default News;
