import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Search = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [country, setCountry] = useState('us'); // Default country is 'us'
  const navigate = useNavigate();

  // Handle form submission
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      // Navigate to News component with query and country parameters
      navigate(`/search?q=${searchQuery}&country=${country}`);
    }
  };

  return (
    <form className="d-flex" onSubmit={handleSearchSubmit} role="search">
      <input
        className="form-control me-2"
        type="search"
        placeholder="Search"
        aria-label="Search"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <select
        className="form-control me-2"
        value={country}
        onChange={(e) => setCountry(e.target.value)}
      >
        <option value="us">USA</option>
        <option value="in">India</option>
        <option value="gb">UK</option>
        {/* Add more country options as needed */}
      </select>
      <button className="btn btn-outline-success" type="submit">Search</button>
    </form>
  );
};

export default Search;
