import React, { useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';

export const Navbar = ({ setCountry }) => {
  const [searchQuery, setSearchQuery] = useState(''); // State to store search query
  const [country, setCountryState] = useState('us'); // Local state for country in Navbar
  const navigate = useNavigate(); // Use navigate hook to navigate to the search results page

  // Handle search query change
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // Handle form submission
  const handleSearchSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission behavior
    if (searchQuery.trim()) {
      // Update the country based on search query
      setCountry(country);
      // Navigate to search results page with query parameter
      navigate(`/search?q=${searchQuery}&country=${country}`);
    }
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <NavLink className="navbar-brand" to="/">Home</NavLink>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              {/* NavLinks here */}
            </ul>
            <form className="d-flex" onSubmit={handleSearchSubmit} role="search">
              <input
                className="form-control me-2"
                type="search"
                placeholder="Search"
                aria-label="Search"
                value={searchQuery}
                onChange={handleSearchChange}
              />
              <select
                className="form-control me-2"
                value={country}
                onChange={(e) => setCountryState(e.target.value)}
              >
                <option value="us">USA</option>
                <option value="in">India</option>
                <option value="gb">UK</option>
                {/* Add more country options as needed */}
              </select>
              <button className="btn btn-outline-success" type="submit">Search</button>
            </form>
          </div>
        </div>
      </nav>
    </div>
  );
};
