import React from 'react'
import { useState } from 'react';
import {Link} from 'react-router-dom';


export const Navbar = () => {

  const[mode,setmode]=useState("light");

  const change=()=>{   
    console.log("SELECTED");
    if(mode=='light'){
      setmode("dark");
      document.body.style.backgroundColor='black';
      document.title="Dark Mode";
    }
    else if(mode=='dark'){
      setmode("light");
      document.body.style.backgroundColor='white';
      document.title="Light Mode";
    }
  };

  return (
    <div className='bg bg-dark' >
      <nav className="navbar navbar-expand-lg navbar-secondary bg-body-tertiary" data-bs-theme={mode} >
  <div className="container-fluid">
    <a className="navbar-brand" href="#">Navbar</a>

    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <Link className="nav-link active" aria-current="page" to="/mode">Home</Link>
        </li>
        <li className="nav-item">
          <Link className="nav-link" to="/form">Link</Link>
        </li>
       
      </ul>
      <div className="form-check form-switch">
  <input className="form-check-input" type="checkbox" role="switch" id="flexSwitchCheckChecked" onClick={change}/>
  <label className="form-check-label" for="flexSwitchCheckChecked"></label>
</div>
      <form className="d-flex" role="search">
        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
        <button className="btn btn-outline-success" type="submit">Search</button>
      </form>
    </div>
  </div>
</nav>



   </div>
  )
}
