import React, { Component } from 'react';
import './App.css';
import { Navbar } from './News/Navbar';
import { News } from './News/News';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoadingBar from 'react-top-loading-bar';
import Search from './News/Search';

export default class App extends Component {
  // Define the state within the class component
  state = {
    progress: 0,
    country: 'us' // Default country is 'us'
  };

  // Define setProgress method using correct syntax
  setProgress = (progress) => {
    this.setState({ progress: progress });
  };

  // Method to update the country
  setCountry = (country) => {
    this.setState({ country });
  };



  render() {
    return (
      <Router>
         <div className={isDarkMode ? 'bg-dark text-light' : 'bg-light text-dark'}>
          <Navbar setCountry={this.setCountry} /> {/* Pass setCountry to Navbar */}
          <LoadingBar color='#f11946' progress={this.state.progress} />

          <Routes>
            {/* Pass setProgress and country to News component */}
            <Route exact path='/' element={<News setProgress={this.setProgress} country={this.state.country} category='general' key="general" />} />
            <Route path="/search" element={<Search setCountry={this.setCountry} />} />
            <Route exact path='/science' element={<News setProgress={this.setProgress} country={this.state.country} category='science' key="science" />} />
            <Route exact path='/sports' element={<News setProgress={this.setProgress} country={this.state.country} category='sports' key="sports" />} />
            <Route exact path='/entertainment' element={<News setProgress={this.setProgress} country={this.state.country} category='entertainment' key="entertainment" />} />
            <Route exact path='/health' element={<News setProgress={this.setProgress} country={this.state.country} category='health' key="health" />} />
            <Route exact path='/technology' element={<News setProgress={this.setProgress} country={this.state.country} category='technology' key="technology" />} />
            <Route exact path='/business' element={<News setProgress={this.setProgress} country={this.state.country} category='business' key="business" />} />
          </Routes>
        </div>
      </Router>
    );
  }
}
