import React, { Component } from 'react'
import NewsItems from './NewsItems'
import { Loading } from './Loading';

export class News extends Component {

  constructor() {
    super();
    this.state = {
      articles: [],
      page: 1,
      totalresults: 0
    }
  }

  async componentDidMount() {
    this.fetchArticles();
  }

  fetchArticles = async () => {
    const pageSize = 12;
    let url = `https://newsapi.org/v2/everything?q=apple&from=2024-10-22&to=2024-10-22&sortBy=popularity&apiKey=73a1cba2fe7e46a984f7b0ecb0d397e5&page=${this.state.page}&pageSize=${pageSize}`;
    this.setState({loading:true})
    let data = await fetch(url);
    let parsedata = await data.json();
    this.setState({
      articles: parsedata.articles,
      totalresults: parsedata.totalResults
    });
  }

  prevpg = async () => {
    this.setState({
      page: this.state.page - 1,
      loading:false

    }, () => {
      this.fetchArticles();
    });
  }

  nxtpg = async () => {
    if (this.state.page < Math.ceil(this.state.totalresults / 12)) {
      this.setState({
        page: this.state.page + 1,
        loading:false
      }, () => {
        this.fetchArticles();
      });
    }
  }

  render() {
    return (
      <div>
        <div className='container my-3'>
          <h2>Headlines</h2>
          {this.state.loading&&<Loading/>}
          <div className='row'>
            {this.state.loadingthis.state.articles.map((element) => {
              return (
                <div className='col-md-4' key={element.url}>
                  <NewsItems
                    title={element.title ? element.title.slice(0, 50) : " "}
                    description={element.description ? element.description.slice(0, 45) : " "}
                    imgUrl={element.urlToImage}
                    url={element.url}
                  />
                </div>
              );
            })}
          </div>
          <div className="container d-flex justify-content-between">
            <button type="button" disabled={this.state.page <= 1} onClick={this.prevpg} className="btn btn-dark">
              &larr; Previous
            </button>
            <button type="button" disabled={this.state.page >= Math.ceil(this.state.totalresults / 12)} onClick={this.nxtpg} className="btn btn-dark">
              Next &rarr;
            </button>
          </div>
        </div>
      </div>
    )
  }
}

export default News;
