import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class News extends Component {

  

  constructor(){
     super();
     this.state={
      articles:[],
      page:1
     }
  }

  async componentDidMount(){
     let url="https://newsapi.org/v2/everything?q=tesla&from=2024-09-23&sortBy=publishedAt&apiKey=73a1cba2fe7e46a984f7b0ecb0d397e5";
    let data=await fetch(url);
    let parsedata=await data.json();
    this.setState({articles:parsedata.articles})
    }

  prevpg =async()=>{
    let url=`https://newsapi.org/v2/everything?q=tesla&from=2024-09-23&sortBy=publishedAt&apiKey=73a1cba2fe7e46a984f7b0ecb0d397e5&page=${this.state.page-1}&pagesize=12`;
    let data=await fetch(url);
    let parsedata=await data.json();
    this.setState({articles:parsedata.articles})
  }

  nxtpg =async()=>{
    let url="https://newsapi.org/v2/everything?q=tesla&from=2024-09-23&sortBy=publishedAt&apiKey=73a1cba2fe7e46a984f7b0ecb0d397e5";
    let data=await fetch(url);
    let parsedata=await data.json();
    this.setState({articles:parsedata.articles})
  }

  render() {
    return (
      <div> 
        <div className='container my-3'>
          <div className='row'>
            <h2>Headlines</h2>
            {this.state.articles.map((elements)=>{
               return <div className='col-md-4'>
               <NewsItems title={elements.title.slice(0,50)} description={elements.description.slice(0,45)} imgUrl={elements.urlToImage} url={elements.url}/>
               </div>
            })}
             
            </div>
            <div class="d-flex justify-content-between">
            <button type="button" disabled={this.state.page<=1} onClick={this.prevpg} class="btn btn-dark">&larr; Previous</button>
            <button type="button" onClick={this.nxtpg} class="btn btn-dark">Next &rarr;</button>
            </div>
            
        </div>
     </div>

    )
  }
}

export default News
