import React, { Component } from 'react'
import NewsItems from './NewsItems'
import { Loading } from './Loading';
import PropTypes from 'prop-types';

export class News extends Component {

  static defaultProps={
    country:'us',
    pageSize:12,
    category:'general',
  }

  static propTypes={
    country:PropTypes.string,
    pageSize:PropTypes.number,
    category:PropTypes.string,
  }


  constructor() {
    super();
    this.state = {
      articles: [],
      page: 1,
      totalresults: 0,
      loading: false // Added loading state to track loading
    }
  }

  async componentDidMount() {
    this.fetchArticles();
  }

  fetchArticles = async () => {
    const pageSize = 12;
    let url = `https://newsapi.org/v2/top-headlines?country=us&category=${this.props.country}&apiKey=73a1cba2fe7e46a984f7b0ecb0d397e5&page=${this.state.page}&pageSize=${pageSize}`;
    this.setState({ loading: true });  // Start loading

    let data = await fetch(url);
    let parsedata = await data.json();

    // Update state with fetched data and stop loading
    this.setState({
      articles: parsedata.articles,
      totalresults: parsedata.totalResults,
      loading: false  // Stop loading after data is fetched
    });
  }

  prevpg = async () => {
    this.setState({
      page: this.state.page - 1,
      loading: true  // Start loading on page change
    }, () => {
      this.fetchArticles();
    });
  }

  nxtpg = async () => {
    if (this.state.page < Math.ceil(this.state.totalresults / 12)) {
      this.setState({
        page: this.state.page + 1,
        loading: true  // Start loading on page change
      }, () => {
        this.fetchArticles();
      });
    }
  }

  render() {
    return (
      <div>
        <div className='container my-3'>
          <h2>Headlines</h2>
          {this.state.loading && <Loading />}  {/* Show loading spinner when fetching */}
          <div className='row'>
            {!this.state.loading && this.state.articles.map((element) => {
              return (
                <div className='col-md-4' key={element.url}>
                  <NewsItems
                    title={element.title ? element.title.slice(0, 50) : " "}
                    description={element.description ? element.description.slice(0, 45) : " "}
                    imgUrl={element.urlToImage}
                    url={element.url}
                  />
                </div>
              );
            })}
          </div>
          <div className="container d-flex justify-content-between">
            <button type="button" disabled={this.state.page <= 1} onClick={this.prevpg} className="btn btn-dark">
              &larr; Previous
            </button>
            <button type="button" disabled={this.state.page >= Math.ceil(this.state.totalresults / 12)} onClick={this.nxtpg} className="btn btn-dark">
              Next &rarr;
            </button>
          </div>
        </div>
      </div>
    )
  }
}

export default News;
